$(document).ready(function() {


	var check_checkbox = function() {
			$(":checkbox").each(function() {
				if ($(this).prop("checked") == true) {
					$(this).parent().parent().css({
						"background-color": "#F5F5F5",
						"border-left": "1px solid #fff"
					});
				} else if ($(this).prop("checked") == false) {
					$(this).parent().parent().css({
						"background-color": "#fff",
						"border-style": "none"
					});
				}
				$(this).click(function() {
					if ($(this).prop("checked") == true) {
						$(this).parent().parent().css({
							"background-color": "#F5F5F5",
							"border-left": "1px solid #fff"
						});
					} else if ($(this).prop("checked") == false) {
						$(this).parent().parent().css({
							"background-color": "#fff",
							"border-style": "none"
						});
					}
				})
			});
		}
	var check_checkbox2 = function() {
			$(".check2").each(function() {
				if ($(this).prop("checked") == true) {
					$(this).parent().parent().css({
						"background-color": "#F5F5F5",
						"border-left": "1px solid #fff"
					});
				} else if ($(this).prop("checked") == false) {
					$(this).parent().parent().css({"background-color":"#fff","border-left":"#fff"});
				}
				$(this).click(function() {
					if ($(this).prop("checked") == true) {
						$(this).parent().parent().css({
							"background-color": "#F5F5F5",
							"border-left": "1px solid #fff"
						});
					} else if ($(this).prop("checked") == false) {
						$(this).parent().parent().css({"background-color":"#fff","border-left":"#fff"});
					}
				})
			});
		}
	// $('a.category').on('click', function() {
	// 	$(this).next().toggle();
	// });
	$('#checkall').click(function() {
		if ($("#checkall").prop("checked") == true) {
			$(":checkbox").prop('checked', true);
			check_checkbox();
		} else {
			$(":checkbox").prop('checked', false);
			check_checkbox();
		}
	});
	$('#checkall2').click(function() {
		if ($("#checkall2").prop("checked") == true) {
			$(".check2").prop('checked', true);
			check_checkbox2();
		} else {
			$(".check2").prop('checked', false);
			check_checkbox2();
		}
	});
	
	check_checkbox();
	check_checkbox2();

	$(".btn-search").on("click",function(){
		$("#form_id").submit();
	})
	$(".table.download tbody > tr > td:not(td:first-child)").on("click",function(){
		$(this).parent().find(":checkbox").click();
	})
	$(".homepage .left-nav .dropdown, .content-footer .dropdown,.usercenter_myarticles #myTab .category.dropdown > ul >li.dropdown").hover(function(){ $(this).addClass("open"); },function(){ $(this).removeClass("open");})


	//登陆页键盘enter进入事件
	$("#password").keydown(function(event){
		if(event.keyCode == 13){
			enter();
		}
	});
	
	/** 禁止图片右击事件（另存为） */
	$("img").on("contextmenu",function(e){
		swal("Sorry...","You have no rights to copy!","warning")
		return false;
	 });


	
})